import 'package:flutter/material.dart';
import 'dart:html' as html;
import 'help.dart';
import 'home.dart';
import 'login.dart';

class Site {
  final String name;
  final String description;
  final String imageUrl;
  final String link;

  Site({
    required this.name,
    required this.description,
    required this.imageUrl,
    required this.link,
  });
}

class SiteRecommendationScreen extends StatelessWidget {
  final List<Site> sites = [
    Site(
      name: 'YouTube',
      description: 'Platform berbagi video yang memungkinkan pengguna untuk mengunggah, menonton, dan berinteraksi dengan video dari seluruh dunia.',
      imageUrl: 'data:image/png;base64,',
      link: 'https://www.youtube.com/',
    ),
    Site(
      name: 'Steam',
      description: 'Platform distribusi digital permainan video yang memungkinkan pengguna untuk membeli, mengunduh, dan memainkan permainan video.',
      imageUrl:
      'data:image/png;base64,',
      link: 'https://store.steampowered.com/',
    ),
    Site(
      name: 'Shopee',
      description: 'Platform e-commerce yang memungkinkan pengguna untuk membeli dan menjual berbagai macam produk secara online.',
      imageUrl: 'data:image/jpeg;base64,',
      link: 'https://shopee.co.id/',
    ),
    Site(
      name: 'Brainly',
      description: 'Platform belajar online yang memungkinkan pengguna untuk bertanya dan menjawab pertanyaan terkait berbagai subjek pelajaran.',
      imageUrl: 'data:image/png;base64,',
      link: 'https://brainly.co.id/',
    ),
    Site(
      name: 'Netflix',
      description: 'Platform streaming yang menawarkan berbagai macam film dan acara televisi, termasuk produksi asli Netflix.',
      imageUrl: 'data:image/jpeg;base64,',
      link: 'https://www.netflix.com/',
    ),
    Site(
      name: 'ESPN',
      description: 'Situs berita olahraga dan layanan streaming yang menawarkan akses ke berbagai jenis olahraga, berita, dan liputan acara.',
      imageUrl:
      'data:image/png;base64,',
      link: 'https://www.espn.com/',
    ),
  ];

  void _launchURL(String url) {
    html.window.open(url, '_blank');
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Site Recommendations'),
        automaticallyImplyLeading: false,
      ),
      body: ListView.builder(
        itemCount: sites.length,
        itemBuilder: (context, index) {
          return Card(
            child: Column(
              children: [
                GestureDetector(
                  onTap: () => _launchURL(sites[index].link),
                  child: Image.network(
                    sites[index].imageUrl,
                    width: double.infinity,
                    height: 300.0,
                    fit: BoxFit.cover,
                  ),
                ),
                SizedBox(height: 15.0),
                Text(
                  sites[index].name,
                  style: TextStyle(
                    fontSize: 30.0,
                    fontWeight: FontWeight.bold,
                  ),
                ),
                SizedBox(height: 15.0),
                Text(sites[index].description),
                SizedBox(height: 15.0),
                GestureDetector(
                  onTap: () => _launchURL(sites[index].link),
                  child: Text(
                    sites[index].link,
                    style: TextStyle(
                      color: Colors.blue,
                      decoration: TextDecoration.underline,
                    ),
                  ),
                ),
                SizedBox(height: 20.0),
              ],
            ),
          );
        },
      ),
      bottomNavigationBar: BottomNavigationBar(
        items: const <BottomNavigationBarItem>[
          BottomNavigationBarItem(
            icon: Icon(Icons.home),
            label: 'Home',
          ),
          BottomNavigationBarItem(
            icon: Icon(Icons.help),
            label: 'Bantuan',
          ),
          BottomNavigationBarItem(
            icon: Icon(Icons.logout),
            label: 'Logout',
          ),
        ],
        selectedItemColor: Colors.blue,
        onTap: (value) {
          switch (value) {
            case 0:
              Navigator.push(
                context,
                MaterialPageRoute(builder: (context) => HomeScreen()),
              );
              break;
            case 1:
              Navigator.push(
                context,
                MaterialPageRoute(builder: (context) => HelpPage()),
              );
              break;
            case 2:
              Navigator.push(
                context,
                MaterialPageRoute(builder: (context) => LoginScreen()),
              );
              break;
            default:
              break;
          }
        },
      ),
    );
  }
}
